function obterTelefone() {
  // Seleciona todos os inputs que possam conter valores de telefone
  var inputs = document.querySelectorAll('input[name="telefone"], input[name="tel"], input[name="phone"]');
  var phone = ""; // Inicializa a variável phone

  // Verifica cada input e retorna o primeiro que tiver um valor
  for (var i = 0; i < inputs.length; i++) {
    if (inputs[i].value) {
      phone = inputs[i].value;
      break; // Para o loop assim que um valor válido é encontrado
    }
  }
  return phone;
}

function obterEmail() {
  // Seleciona todos os inputs que possam conter valores de email
  var inputs = document.querySelectorAll('input[name="email"], input[name="e-mail"]');
  var email = ""; // Inicializa a variável email

  // Verifica cada input e retorna o primeiro que tiver um valor
  for (var i = 0; i < inputs.length; i++) {
    if (inputs[i].value) {
      email = inputs[i].value;
      break; // Para o loop assim que um valor válido é encontrado
    }
  }
  return email;
}

